duplication.codefragment:
  suggestions.remove(toMatch);
  toPrint = Constants.PRINT_NUM - 1;
}

List<Candidate> candidates = new ArrayList<Candidate>();
if (!smartRank) {
  candidates = eng.makeBasicCandidates(suggestions, prev);
  Collections.sort(candidates, new Ranker<Candidate>());
} else {
  candidates = eng.makeSmartCandidates(suggestions, prev, toMatch);
  Collections.sort(candidates, new SmartRanker<Candidate>());
}
int len = Math.min(toPrint, candidates.size());
